lType":"address[]","name":"","type":"address[]"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"address","name":"","type":"address"}],"name":"isAdmin","outputs":[{"internalType":"bool","name":"","type":"bool"}],"stateMutability":"view","type":"function"},{"inputs":[{"internalType":"address","name":"project","type":"address"},{"internalType":"string","name":"reason","type":"string"}],"name":"rejectProject","outputs":[],"stateMutability":"nonpayable","type":"function"},{"inputs":[{"internalType":"address","name":"adminToRemove","type":"address"}],"name":"removeAdmin","outputs":[],"stateMutability":"nonpayable","type":"function"}],"devdoc":{"kind":"dev","methods":{},"version":1},"userdoc":{"kind":"user","methods":{},"version":1}},"settings":{"compilationTarget":{"contracts/CrowdfundAdmin.sol":"CrowdfundAdmin"},"evmVersion":"istanbul","libraries":{},"metadata":{"bytecodeHash":"ipfs","useLiteralContent":true},"optimizer":{"enabled":true,"runs":200},"remappings":[]},"sources":{"contracts/CrowdfundAdmin.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "./interfaces/ICrowdfundAdmin.sol";
import "./interfaces/ICrowdfundProject.sol";
import "./libraries/CrowdfundStructs.sol";
import "./libraries/CrowdfundErrors.sol";

contract CrowdfundAdmin is ICrowdfundAdmin {
    address public admin;
    address[] public allProjects;
    mapping(address => bool) public isAdmin;
    event ProjectApproved(address indexed project, address indexed approver, string reason);
    event ProjectRejected(address indexed project, address indexed rejector, string reason);
    event AdminAdded(address indexed admin);
    event AdminRemoved(address indexed admin);

    constructor() {
        admin = msg.sender;
        isAdmin[msg.sender] = true;
    }

    modifier onlyAdmin() {
        require(isAdmin[msg.sender], CrowdfundErrors.NOT_ADMIN);
        _;
    }
    function checkAdmin(address adminAddress) external view override returns (bool) {
        return isAdmin[adminAddress];
    }
    function addAdmin(address newAdmin) external override onlyAdmin {
        require(!isAdmin[newAdmin], "Address is already an admin");
        isAdmin[newAdmin] = true;
        emit AdminAdded(newAdmin);
    }


    function removeAdmin(address adminToRemove) external override onlyAdmin {
        require(adminToRemove != admin, "Cannot remove primary admin");
        require(isAdmin[adminToRemove], "Address is not an admin");
        isAdmin[adminToRemove] = false;
        emit AdminRemoved(adminToRemove);
    }

    function approveProject(address project, string calldata reason) external override onlyAdmin {
        ICrowdfundProject(project).approveProject();
        allProjects.push(project);
        emit ProjectApproved(project, msg.sender, reason);
    }

    function rejectProject(address project, string calldata reason) external override onlyAdmin {
        ICrowdfundProject(project).rejectProject(reason);
        emit ProjectRejected(project, msg.sender, reason);
    }
  
    function getAdminProjects() external view override returns (address[] memory) {
        return allProjects;
    }
}","keccak256":"0xef061d9adbf24c3d0a492ffef7c425a2b252b9d9ff9c4ba9f7dc8d9de4295c43","license":"UNLICENSED"},"contracts/interfaces/ICrowdfundAdmin.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "../libraries/CrowdfundStructs.sol";

interface ICrowdfundAdmin {
    function addAdmin(address newAdmin) external;
    function removeAdmin(address adminToRemove) external;
    function approveProject(address project, string calldata reason) external;
    function rejectProject(address project, string calldata reason) external;
    function getAdminProjects() external view returns (address[] memory);
    function checkAdmin(address adminAddress) external view returns (bool); 
}","keccak256":"0x0c15326c02f55a6eff6fe8e43649f056e8bb61977a84769ac0cea073d3e22ed2","license":"UNLICENSED"},"contracts/interfaces/ICrowdfundProject.sol":{"content":"// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "../libraries/CrowdfundStructs.sol";

interface ICrowdfundProject {
    function approveProject() external;
    function rejectProject(string memory reason) external;
    function getProjectInfo() external view returns (CrowdfundStructs.ProjectInfo memory);
    function getStatus() external view returns (CrowdfundStructs.ProjectStatus);
    function getTotalRaised() external view returns (uint256);
    function getInvestors() external view returns (address[] memory);
    function getInvestment(address investor) external view returns (uint256);
    function getReturnInfo() external view returns (CrowdfundStructs.ProjectReturn memory);
    function getPaymentToken() external view returns (address);
